mapper.select:
select
  id, username, password, user_profile, user_state_id, is_enabled, is_locked,role,gender,name,student_id,nation,hometown
from chatroom.user


        and id = #{id}


        and username = #{username}


        and password = #{password}


        and user_profile = #{userProfile}


        and user_state_id = #{userStateId}


        and is_enabled = #{isEnabled}


        and is_locked = #{isLocked}

select * from user where 1=1

    and is_locked =#{isLocked}


    limit #{page},#{size}
